                /// Add chat image.
                &::before {
                    content: ''
                    filter: invert(var(--chat-bg-i)) opacity(var(--chat-bg-o))
                    background: var(--chat-image) i
                    position: absolute
                    height: 100%
                    width: 100%
                }

                /// Add pseudo text.
                span {
