            }

            /// Fix borders an unknown contact.
            ._3ImlL {
                border-top: none
                border-bottom: 1px solid bg5
            }

            /// Groups -> Only admins can send messages.
            ._31enr {
                border-top: 1px solid bg5 i
                border-left: @border-top
            }

