                    /// Status indicator.
                    { status } {
                        left: 44px
                        z-index: 9999
                        position: fixed
                        margin-top: 4px
                        border: 2px solid bg2 i
                    }

                    /// Unread indicator.
                    { unread } {
                        left: 44px
                        z-index: 9999
                        position: fixed
                        margin-top: 4px
                        padding: 3px i
                        border: 2px solid bg2 i
